                     \documentclass{aastex} \usepackage{amsbsy} \usepackage{amsfonts} \usepackage{amssymb} \usepackage{bm} \usepackage{mathrsfs} \usepackage{pifont} \usepackage{stmaryrd} \usepackage{textcomp} \usepackage{portland,xspace} \usepackage{amsmath,amsxtra} \usepackage[OT2,OT1]{fontenc} \newcommand\cyr{ \renewcommand\rmdefault{wncyr} \renewcommand\sfdefault{wncyss} \renewcommand\encodingdefault{OT2} \normalfont \selectfont} \DeclareTextFontCommand{\textcyr}{\cyr} \pagestyle{empty} \DeclareMathSizes{10}{9}{7}{6} \begin{document} \landscape $\theta =[ \mathrm{vec}\,( B_{FR}) ^{\prime },\mathrm{vec}\,( B_{FZ}) ^{\prime },\mathrm{vec}\,( B_{RZ.F}) ^{\prime },\mathrm{vech}\,( \Sigma _{FF}) ^{\prime },\mathrm{vech}\,( \Sigma _{RR.F}) ^{\prime },\mathrm{vech}\,( \Sigma _{ZZ.R.F}) ^{\prime }] ^{\prime }$ \end{document}
